'use strict';

const data = require('./shared/pinceau.7cd078d1.cjs');
const palette = require('./shared/pinceau.f7625e1a.cjs');
require('chroma-js');

function defineTheme(config) {
  const mqKeys = ["dark", "light", ...Object.keys(config?.media || [])];
  config = data.normalizeConfig(config, mqKeys, false);
  return config;
}

exports.get = data.get;
exports.palette = palette.palette;
exports.defineTheme = defineTheme;
